      <div id="counter">count:{count}</div>
      <button
        id="addBtn"
        onClick={() => {
          dispatch({ type: "countReducer/INCREMENT" });
        }}
      >
        addBtn
      </button>
      <button
        id="minusBtn"
        onClick={() => {
          dispatch({ type: "countReducer/DECREMENT" });
        }}
      >
        minusBtn
      </button>
    </>
  );
};

const connectedPage = connect(({ count }) => ({ count }))(Page);
export default connectedPage;

