//所有打印的num都是0
export default () => {
  const [num, setNum] = useState(0);
  const handerClick = () => {

    setNum(num + 1);
    console.log(num);
    setNum(num + 1);
    console.log(num);
    setNum(num + 1);
    console.log(num);

    setTimeout(() => {
      setNum(num + 1);
      console.log(num);
      setNum(num + 1);
      console.log(num);
      setNum(num + 1);
      console.log(num);
    }, 1000);

    Promise.resolve().then(() => {
      setNum(num + 1);
      console.log(num);
      setNum(num + 1);
      console.log(num);
      setNum(num + 1);
      console.log(num);
    });
  };

  useLayoutEffect(() => {
    const dom = document.getElementById("button");
    if (!dom) return;
    dom.addEventListener("click", handerClick);
  }, []);

  return (
    <div>
      <button id="button">{num}</button>
    </div>
  );
};
