export default function App() {
  const ref = useRef();

  // 可视区域高度
  const containerHeight = document.body.clientHeight;
  // 可显示的列表项数
  const visibleCount = Math.ceil(containerHeight / itemHeight);

  const [listData, setListData] = useState([]);
